mapper.sql:
/* 返回消息ID */

    AND ID = #{id}

/* 返回消息内容 */

    AND MSG_CONTENT = #{msgContent}

/* 返回消息类型(text:文本;music:音乐;news:图文;) */

    AND MSG_TYPE = #{msgType}

/* 消息标题 */

    AND MSG_TITLE = #{msgTitle}

/* 消息图片链接 */

    AND MSG_PIC = #{msgPic}

/* 点击跳转链接 */

    AND SKIP_URL = #{skipUrl}

mapper.update:
UPDATE lhb_resp_message

    /* 返回消息内容 */

        MSG_CONTENT = #{msgContent},

    /* 返回消息类型(text:文本;music:音乐;news:图文;) */

        MSG_TYPE = #{msgType},

    /* 消息标题 */

        MSG_TITLE = #{msgTitle},

    /* 消息图片链接 */

        MSG_PIC = #{msgPic},

    /* 点击跳转链接 */

        SKIP_URL = #{skipUrl},

    /* 返回消息ID */

        ID = #{id},


WHERE ID = #{id}
